import {Injectable} from '@angular/core'
import {HttpClient, HttpErrorResponse, HttpParams} from '@angular/common/http'
import {catchError, delay, Observable, retry, tap, throwError} from 'rxjs'
import {IEvent} from '../models/event'

@Injectable({
  providedIn: 'root'
})
export class EventsService {
  constructor(
    private http: HttpClient
  ) {
  }

  events: IEvent[] = []

  getAll(): Observable<IEvent[]> {
    console.log(this.http.get<IEvent[]>('http://localhost:3003/events')
    return this.http.get<IEvent[]>('http://localhost:3003/events', {
      params: new HttpParams({
        fromObject: {limit: 5}
      })
    }).pipe(
      delay(200),
      retry(2),
      tap(events => this.events = events)
    )
  }

  create(event: IEvent): Observable<IEvent> {
    return this.http.post<IEvent>('http://localhost:3003/events', event)
      .pipe(
        tap(prod => this.events.push(prod))
      )
  }
}
