    program: "test1",
    organizer: "COMPANY",
    partners: "test1",
    address: "test1",
    price: "990000.00",
    date_start: "2023-08-28T10:00:00Z",
    date_end: "2023-08-29T18:00:00Z",
    created_at: "2023-07-24T11:27:52.446199Z",
    city: "test1",
    isLiked: false,
    tags: [
      {
        id: 2,
        name: "C++",
        slug: "c-plus-plus"
      },
      {
        id: 3,
        name: "Docker",
        slug: "docker"
      },
      {
        id: 1,
        name: "Python",
