<div class="py-4 px-6 border rounded flex items-center flex-col mb-2">
  <img [src]="event.image" [alt]="event.title" class="w-1/6 mb-2">
  <h2 class="mb-2 text-md text-center">{{ event.title }}</h2>
  <p>Price: <span class="font-bold">{{ event.price | currency:'RUB' }}</span></p>

  <button
    class="border py-2 px-4 rounded"
    [ngClass]="{
      'bg-yellow-400': !details,
      'bg-blue-400': details
    }"
    (click)="details = !details"
  >
    {{ details ? 'Hide' : 'Show' }} Details
  </button>

  <div *ngIf="details">
    <p>{{ event.description }}</p>
  </div>
</div>
